document.addEventListener("DOMContentLoaded", function () {
    document.querySelectorAll(".learn-more-btn").forEach(button => {
        button.addEventListener("click", function () {
            const moreInfo = this.parentElement.querySelector(".more-info"); // Находим правильный блок

            if (moreInfo.classList.contains("d-none")) {
                moreInfo.classList.remove("d-none");
                moreInfo.classList.add("d-block");
                this.textContent = "Show Less"; // Меняем текст кнопки
            } else {
                moreInfo.classList.remove("d-block");
                moreInfo.classList.add("d-none");
                this.textContent = "Learn More"; // Возвращаем текст кнопки
            }
        });
    });
});
